import React, { useState } from 'react'

import SweetAlert from 'sweetalert2'
function Login() {
    const adminEmail = 'saadmansuri@gmail.com'
    const adminPwd = 'saad123'
    
    const [enteredEmail, setEnteredEmail] = useState('saad@gmail.com')
    const [enteredPwd, setEnteredPwd] = useState('saad123')

    const handlerSubmit = e => {
        e.preventDefault()
        console.log(enteredEmail, enteredPwd);
        if(adminEmail === enteredEmail && adminPwd === enteredPwd){
            SweetAlert.fire({
                timer : 1500,
                showConfirmButton: false,
                willOpen: ()=>{
                    SweetAlert.showLoading()
                }
            })
        }
    }

    return (
        <div className="small-container">
            <form onSubmit={handlerSubmit}>
                <h1>Admin Login</h1>
                <div>
                    <label htmlFor="email">Email</label>
                    <input type="email" onChange={ e => setEnteredEmail(e.target.value)} />
                </div>
                <div>
                    <label htmlFor="password">Password</label>
                    <input type="text" onChange={ e => setEnteredPwd(e.target.value)}/>
                </div>
                <div>
                    <input type="submit" value="Login"/>
                </div>
            </form>
        </div>
    )
}

export default Login
