import {React, useState} from 'react'
import Card from '../UI/Card';
import classes from '../Css/AddUser.module.css'
import Button from '../UI/Button';

const AddUsers =(props) =>{

    const [enteredUsername, setEnterUsername] = useState('')
    const [enteredAge, setEnterAge] = useState('')
    const [error, setError] = useState()

    const usernameChangeHandler = e =>{
        setEnterUsername(e.target.value)
    }
    const ageChangeHandler = e =>{
        setEnterAge(e.target.value)
    }

    const addUserHandler = event => {
        event.preventDefault()
        if(enteredUsername.trim().length === 0 || enteredAge.trim().length === 0){
            // return alert('Please Enter Some Value')
            setError(
                {
                    title : 'Invalid Input',
                    message : 'Please enter a valid '
                }
            )
        }
        if(+enteredAge < 1){
            return alert('Please Enter Positive Age')
        }
        props.onAddUser(enteredUsername, enteredAge)    
        // console.log(enteredUsername, +enteredAge);
        // setEnterUsername('')
        // setEnterAge('')
    }
    return (
        <Card className={classes.input}>
            <form onSubmit={addUserHandler}>
                <label htmlFor="username">Username</label>
                <input type="text" name="" value={enteredUsername} id="username" onChange={usernameChangeHandler}/>
                <label htmlFor="age">Age</label>
                <input type="number" name="" value={enteredAge} id="age" onChange={ageChangeHandler}/>
                <Button type='submit'>Add User</Button>
            </form>
        </Card>
    )
}

export default AddUsers
