import React, { useEffect, useState } from 'react'
import Form from './Form'

const AddUsers = (props) => {
    const [enterInput, setEnteredInput] = useState({
        fname :'',
        sname: ''
    })
    const inputChangeHandler = (e) => {
        setEnteredInput({
            fname : e.target.value,
            sname : e.target.value
        })
        // console.log(e.target.value);
    }
    useEffect(()=> {
        const json = widnow.localStorage.getItem()
    })
    const formSubmitHandler = e => {
        e.preventDefault()
        props.onAddUser(enterInput)
    }
    return (
        <Form formSubmitHandler={formSubmitHandler} inputChangeHandler={inputChangeHandler}/>
    )
}

export default AddUsers
