import React, { useEffect, useState } from 'react'
import Form from './Form'

const AddUsers = (props) => {
    const [todos, setTodos] = useState([]);
    const [todo, setTodo] = useState("");

    const [enterInput, setEnteredInput] = useState({
        fname :'',
        sname: ''
    })
    const inputChangeHandler = (e) => {
        setEnteredInput({
            fname : e.target.value,
            sname : e.target.value
        })
        // console.log(e.target.value);
    }
    useEffect(()=> {
        const json = localStorage.getItem('todos')
        const loadedTodos = JSON.parse(json)
        console.log(loadedTodos);
        if(loadedTodos){
            setTodos(loadedTodos)
        }
    }, [])

    useEffect(()=>{
        const json = JSON.stringify(todos)
        localStorage.setItem('todos', json)
    }, [todos])

    const formSubmitHandler = e => {
        e.preventDefault()
        setTodos([...todos].concat(enterInput));
        console.log(enterInput);
        console.log(todos);
        // console.log([...todos].concat(enterInput));
        return
        const getUsersList = JSON.parse(localStorage.getItem('todos'))
        // setTodo("");
        props.onAddUser(getUsersList)
    }
    return (
        <Form formSubmitHandler={formSubmitHandler} inputChangeHandler={inputChangeHandler}/>
    )
}

export default AddUsers
